import swiper from "../sub/swiper.vue";
import { Toast } from "mint-ui"; //引入 mui js 库,需下载第三方模块，禁止脚手架严格模式
//import mui from "../../lib/mui/js/mui.js";

export default {
  data: function data() {
    return {
      imglist: [],
      val: 1,
      info: [],
      id: this.$route.params.id
    };
  },
  methods: {
    addCartTo: function addCartTo() {
      var _this = this;

      //1.将商品编号数量保存在服务器
      var id = this.id;
      var count = this.val;
      this.$http.get("addCart?pid=" + id + "&count=" + count).then(function (result) {
        if (result.body.code == 1) {
          //更新购物车中商品的数量
          //修改Vuex共享数据
          _this.$store.commit("increment", count);

          Toast(result.body.msg);
        } else {
          Toast(result.body.msg);
        }
      }); //2.更新App.vue组件购物车数量
    },
    add: function add() {
      if (this.val < 999) {
        this.val++;
      }
    },
    sub: function sub() {
      if (this.val > 1) {
        this.val--;
      }
    },
    getdetail: function getdetail() {
      var _this2 = this;

      this.$http.get("goodlist").then(function (result) {
        var id = _this2.id - 1;
        _this2.info = result.body[id];
      });
    },
    getImageList: function getImageList() {
      var _this3 = this;

      this.$http.get("imagelist").then(function (result) {
        _this3.imglist = result.body;
      });
    }
  },
  created: function created() {
    //console.log(this.$route.params.id);
    this.getImageList();
    this.getdetail();
  },
  components: {
    "swiper-box": swiper
  }
};
