export default {
  data: function data() {
    return {
      list: []
    };
  },
  methods: {
    jumpdetail: function jumpdetail(id) {
      //参数：商品的id
      //console.log(id);
      this.$router.push("/home/goodsinfo/" + id);
    },
    getInfo: function getInfo() {
      var _this = this;

      var url = "http://127.0.0.1:3000/goodlist";
      this.$http.get(url).then(function (result) {
        //console.log(result);
        _this.list = result.body;
      });
    }
  },
