//单独引入Toast组件
import { Toast } from "mint-ui";
export default {
  data: function data() {
    return {
      msg: "",
      //双向绑定用户评论 
      pageIndex: 0,
      //当前页码
      pageSize: 5,
      //页大小
      list: [] //分页数据 

    };
  },
  created: function created() {
    this.getMore();
  },
  methods: {
    getMore: function getMore() {
      var _this = this;

      //1:页码自增
      this.pageIndex++;
      var p = this.pageIndex; //页码

      var s = this.pageSize; //页大小

      var id = this.id; //新闻编号
      //2:发送ajax  /getComment

      var url = "getComment?id=" + id + "&pno=" + p + "&pageSize=" + s; //3:参数 id pno pageSize
      //4:获取返回数据保存list属性

      this.$http.get(url).then(function (result) {
        var row = _this.list.concat(result.body.data);

        _this.list = row;
      }); //5:显示模板中
    },
    postComment: function postComment() {
      var _this2 = this;

      //1:获取新闻编号
      var nid = this.id; //2:获取评论内容

      var pmsg = this.msg; //3:判断新闻评论内容不能为空,提示用户

      if (pmsg.trim().length == 0) {
        Toast("评论内容不能为空!");
        return;
      } //4:发送post请求


      var url = "http://127.0.0.1:3000/postcomment";
      this.$http.post(url, {
        nid: nid,
        msg: pmsg
      }).then(function (result) {
        Toast("发表成功"); //考虑当前页码?
        //清空pageIndex
        //考虑list:[]

        _this2.pageIndex = 0; //最新的评论存进第一页

        _this2.list = []; //把第一页的内容清空重新刷新

        _this2.getMore();
      });
    }
  },
  props: ["id"]
};
