import { Toast } from "mint-ui";
export default {
  data: function data() {
    return {
      msg: "",
      pageIndex: 0,
      pageSize: 5,
      list: []
    };
  },
  props: ["id"],
  created: function created() {
    this.getMore();
  },
  methods: {
    getMore: function getMore() {
      var _this = this;

      //1:页码自增
      this.pageIndex++;
      var p = this.pageIndex; //页码

      var s = this.pageSize; //页大小

      var id = this.id; //新闻编号
      //2:发送ajax  /getComment

      var url = "getComment?id=" + id + "&pno=" + p + "&pageSize=" + s; //3:参数 id pno pageSize
      //4:获取返回数据保存list属性

      this.$http.get(url).then(function (result) {
        var row = _this.list.concat(result.body.data);

        _this.list = row;
      }); //5:显示模板中
    },
    postcomment: function postcomment() {
      //1.获取新闻编号
      var nid = this.id; //2.获取评论内容

      var pmsg = this.msg; //3.判断新闻评论内容不能为空

      if (pmsg.trim().length == 0) {
        Toast("评论内容不能为空!");
        return;
      } //4.发送post请求


      var url = "http://127.0.0.1:3000/postcomment";
      this.$http.post(url, {
        nid: nid,
        msg: pmsg
      }).then(function (result) {
        console.log(result);
      }); //5.获取服务器程序返回
      //6.提示用户"评论成功"
    }
  }
};
