export default {
  data: function data() {
    return {
      list: [],
      pageIndex: 0,
      //属性显示当前页数
      pageSize: 5,
      //页大小
      hasMore: true,
      //是否存在下一页数据
      pageCount: 2 //总页数 

    };
  },
  methods: {
    //加载更多
    getMore: function getMore() {
      var _this = this;

      this.pageIndex++; //判断是否有下一页数据

      this.hasMore = this.pageIndex <= this.pageCount; //如果没有下一页数据停止函数执行

      if (!this.hasMore) {
        return;
      }

      var url = "http://127.0.0.1:3000/newslist";
      url += "?pno=" + this.pageIndex + "&pageSize=" + this.pageSize;
      this.$http.get(url).then(function (result) {
        //concat 接拼数组 将二个数组拼接新数组
        var rows = _this.list.concat(result.body.data);

        _this.list = rows; //保存总页数

        _this.pageCount = result.body.pageCount;
      });
    }
  },
  created: function created() {
    this.getMore();
  }
};
